//import 'react-tabs/style/react-tabs.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import 'bootstrap/dist/js/bootstrap.min';
import axios from "axios";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import PieMongo from './componentes/PieMongo';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import TablaMongo from './componentes/TablaMongo';
import React, { Component } from 'react'; import './App.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';




import {
    Route,
    NavLink,
    BrowserRouter
} from "react-router-dom";

var datos = [];
datos.push({ name: 'lopesyes', location: 'sac', age: 17, infectedtype: 'comu', state: 'maso' });

class App extends Component {
    constructor() {
        super();
        this.state = {
            data: []
        };
        this.getData = this.getData.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:4000/app/getData')
            .then((res) => {
                this.setState({
                    data: res.data
                })
            });

    }


    getData() {
        console.log('Data recibida');
        axios.get('http://localhost:4000/app/getData')
            .then((res) => {
                this.setState({
                    data: res.data
                })
            });
        console.log(this.state.data);
    }

    render() {
        return (
            <div>
                <Navbar bg="dark" variant="dark">
                    <button className="d-lg-none toggle-sidebar"><span className="navbar-toggler-icon"></span></button>
                    <Navbar.Brand href="/">Proyecto 2 Sopes 1</Navbar.Brand><span className="hidden-xs text-muted">React</span>
                </Navbar>
                <BrowserRouter>
                    <Row>
                        <Nav to="/" className="flex-sm-column" id="sidebar">
                            <ListGroup className="nav nav-sidebar flex-sm-column">
                                <ListGroup.Item>
                                    <a href="#overview" data-toggle="collapse" aria-expanded="false" className="dropdown-toggle"><span>OVERVIEW</span></a>
                                </ListGroup.Item>
                                <ListGroup>
                                    <ListGroup className="sub-menu collapse" id="lineCharts">
                                        <ListGroup.Item> <NavLink to="/line-chart">Line Chart</NavLink></ListGroup.Item>
                                        <ListGroup.Item> <NavLink to="/spline-chart">Spline Chart</NavLink></ListGroup.Item>
                                        <ListGroup.Item> <NavLink to="/step-line-chart">Step Line Chart</NavLink></ListGroup.Item>
                                    </ListGroup>
                                </ListGroup>
                            </ListGroup>

                        </Nav>


                        <Col xl={{ span: 7, offset: 3 }} lg={{ span: 8, offset: 3 }} xs={{ span: 8, offset: 2 }}>
                            <Container>
                                <div className="content">
                                    <Route path="/line-chart" component={TablaMongo} />
                                </div>
                            </Container>
                        </Col>
                    </Row>
                </BrowserRouter>


                <Tabs>
                    <TabList>
                        <Tab>Reporte 1</Tab>
                        <Tab>Reporte 2</Tab>
                        <Tab>Reporte 3</Tab>
                        <Tab>Reporte 4</Tab>
                        <Tab>Reporte 5</Tab>
                        <Tab>Reporte 6</Tab>
                        <Tab>Deporte 7</Tab>
                    </TabList>
                    <TabPanel>
                        <h2>Datos almacenados en en MongoDB</h2>
                        <div className="row">
                            <button type="button" className="btn btn-success btn-lg" onClick={this.getData}>Actualizar</button>
                        </div>
                        <TablaMongo datosTabla={this.state.data} />
                    </TabPanel>
                    <TabPanel>
                        <h2>Los diez países con más vacunados, en Redis</h2>

                    </TabPanel>
                    <TabPanel>
                        <h2>Personas vacunadas por cada país, en Redis</h2>

                    </TabPanel>
                    <TabPanel>
                        <h2>Gráfica de pie de los géneros de los vacunados por país, en MongoDB</h2>
                        <PieMongo datosTabla={this.state.data} />
                    </TabPanel>
                    <TabPanel>
                        <h2>Los últimos cinco vacunados almacenados por país, en MongoDB</h2>

                    </TabPanel>
                    <TabPanel>
                        <h2>Gráfica de barras del rango de edades (de diez en diez) por cada país, en Redis</h2>

                    </TabPanel>
                    <TabPanel>
                        <h2>Mapa interactivo que muestre estos reportes</h2>

                    </TabPanel>
                </Tabs>


            </div>
        );
    }
}

export default App;
